const alchemyKey = "wss://eth-sepolia.g.alchemy.com/v2/ILI-RxKQ1Ij2yE2bvm5ELkDDRgKdsPLH";
const { createAlchemyWeb3 } = require("@alch/alchemy-web3");
const web3 = createAlchemyWeb3(alchemyKey);
const contractABI = require('../contract-abi.json');
const contractAddress = '0xD7b942a91521d46a9D1E584ADD1100dEb0e6D5d0';

export const helloWorldContract = new web3.eth.Contract(
    contractABI,
    contractAddress
);

export const loadCurrentMessage = async () => { 
  const message = await helloWorldContract.methods.message().call();
  return message;
};

export const connectWallet = async () => {
  if (window.ethereum) {
    try {
      const addressArray = await window.ethereum.request({
        method: "eth_requestAccounts",
      });
      const obj = {
        status: "üëÜüèΩ Write a message in the text-field above.",
        address: addressArray[0],
      };
      return obj;
    } catch (err) {
      return {
        address: "",
        status: "üòû" + err.message, // Added sad face
      };
    }
  } else {
    return {
      address: "",
      status: (
        <span>
          <p>
            {" "}
            ü¶ä{" "} {/* Added fox */}
            <a target="_blank" href={`https://metamask.io/download.html`}>
              You must install Metamask, a virtual Ethereum wallet, in your
              browser.
            </a>
          </p>
        </span>
      ),
    };
  }

};

export const getCurrentWalletConnected = async () => {
  if (window.ethereum) {
    try {
      const addressArray = await window.ethereum.request({ 
        method: "eth_accounts"
      });
      if (addressArray.length > 0) {
        return {
          address: addressArray[0],
          status: "üëÜ Write a message in the text-field above."
        };
      } else {
        return {
          address: "",
          status: "ü¶ä Connect to Metamask using the top right button."
        };
      }
    } catch (err) {
      return {
        address: "",
        status: "üò¢ " + err.message // Added sad crying emoji
      };
    }
  } else {
    return {
      address: "",
      status: (
        <span>
          <p>
            {" "}
            ü¶ä{" "} {/* Added fox emoji */}
            <a target="_blank" href="https://metamask.io/download">You must install Metamask, a virtual Ethereum wallet, in your browser.</a>
          </p>
        </span>
      )
    };
  }
};



export const updateMessage = async (address, message) => {
  if (!window.ethereum || address === null) {
    return {
      status: "Connect your Metamask wallet to update the message on the Blockchain.",
    };
  }

  if (message.trim() === "") {
    return {
      status: "‚ùå Your message cannot be an empty string!",
    };
  }

  // Set up transaction parameters
  const transactionParameters = {
    to: contractAddress,
    from: address,
    data: helloWorldContract.methods.update(message).encodeABI(),
  };

  // Sign the transaction
  try {
    const txHash = await window.ethereum.request({
      method: "eth_sendTransaction",
      params: [transactionParameters],
    });
    return {
      status: (
        <span>
          ‚úÖ {""}
          <a target="_blank" href={`https://sepolia.etherscan.io/tx/${txHash}`}>View the status of your transaction on Etherscan!</a>
          <br />
          ‚ÑπÔ∏è Once the transaction is verified by the network, the message will be updated automatically.
        </span>
      ),
    };
  } catch (error) {
    return {
      status: "üò¢ " + error.message,
    };
  }
};
