import React, { useState, useEffect } from "react";
import {
  addTeamMember,
  assignTask,
  completeTask,
  deactivateTeamMember,
  releasePayment,
  getTeamMembers,
  withdrawBalance,
  connectToMetaMask
} from "./util/interact.js";

const ContractTeamManagement = () => {
  // Define state variables
  const [walletAddress, setWalletAddress] = useState("");
  const [name, setName] = useState("");
  const [taskDescription, setTaskDescription] = useState("");
  const [amount, setAmount] = useState("");
  const [teamMembers, setTeamMembers] = useState([]);

  // Function to handle adding a team member
  const handleAddTeamMember = async (e) => {
    e.preventDefault();
    await addTeamMember(walletAddress, name);
    // Optionally, update UI or perform other actions after adding a team member
  };

  // Function to handle assigning a task
  const handleAssignTask = async (e) => {
    e.preventDefault();
    await assignTask(walletAddress, taskDescription);
    // Optionally, update UI or perform other actions after assigning a task
  };

  // Function to handle completing a task
  const handleCompleteTask = async (e) => {
    e.preventDefault();
    await completeTask(walletAddress);
    // Optionally, update UI or perform other actions after completing a task
  };

  // Function to handle deactivating a team member
  const handleDeactivateTeamMember = async (e) => {
    e.preventDefault();
    await deactivateTeamMember(walletAddress);
    // Optionally, update UI or perform other actions after deactivating a team member
  };

  // Function to handle releasing payment
  const handleReleasePayment = async (e) => {
    e.preventDefault();
    await releasePayment(walletAddress, amount);
    // Optionally, update UI or perform other actions after releasing payment
  };

  // Function to fetch team members
  const fetchTeamMembers = async () => {
    const members = await getTeamMembers();
    setTeamMembers(members);
  };

  // Fetch team members when component mounts
  useEffect(() => {
    fetchTeamMembers();
  }, []);

  // Connecting to MetaMask
  const connectToMetaMask = async () => {
    if (typeof window.ethereum !== "undefined") {
      try {
        const accounts = await window.ethereum.request({
          method: "eth_requestAccounts"
        });
        console.log("Connected to MetaMask:", accounts[0]);
        setConnectedAddress(accounts[0]);
      } catch (error) {
        console.error("Error connecting to MetaMask:", error);
      }
    } else {
      console.error("MetaMask not detected");
    }
  };

  return (
    <div className="team-management-container">
      <h1>Team Management Application</h1>
      <div className="column">
        <button className="connect-metamask" onClick={connectToMetaMask}>Connect to MetaMask</button>
      </div>
      <div className="row">
        <div className="column">
          <h2>All Team Members and Their Tasks</h2>
          <table>
            <thead>
              <tr>
                <th>Address</th>
                <th>Name</th>
                <th>Task</th>
              </tr>
            </thead>
            <tbody>
              {teamMembers.map((member) => (
                <tr key={member.address}>
                  <td>{member.address}</td>
                  <td>{member.name}</td>
                  <td>{member.tasks.map((task) => task.description).join(", ")}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div className="column">
          <h2>Add Team Member</h2>
          <form>
            <label>
              Wallet Address:
              <input type="text" name="walletAddress" />
            </label>
            <br />
            <label>
              Name:
              <input type="text" name="name" />
            </label>
            <br />
            <button type="submit">Add Team Member</button>
          </form>
        </div>
        <div className="column">
          <h2>Assign Task</h2>
          <form>
            <label>
              Team Member Address:
              <input type="text" name="teamMember" />
            </label>
            <br />
            <label>
              Task Description:
              <input type="text" name="taskDescription" />
            </label>
            <br />
            <button type="submit">Assign Task</button>
          </form>
        </div>
        <div className="column">
          <h2>Complete Task</h2>
          <form>
            <label>
              Team Member Address:
              <input type="text" name="teamMember" />
            </label>
            <br />
            <button type="submit">Complete Task</button>
          </form>
        </div>
      </div>
      <div className="row">
        <div className="column">
          <h2>Deactivate Team Member</h2>
          <form>
            <label>
              Team Member Address:
              <input type="text" name="teamMember" />
            </label>
            <br />
            <button type="submit">Deactivate Team Member</button>
          </form>
        </div>
        <div className="column">
          <h2>Release Payment</h2>
          <form>
            <label>
              Team Member Address:
              <input type="text" name="teamMember" />
            </label>
            <br />
            <label>
              Amount:
              <input type="text" name="amount" />
            </label>
            <br />
            <button type="submit">Release Payment</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ContractTeamManagement;
