                    points="88.875 58.5 64.688 78.75 46.125 101.25 92.25 102.938"
                  ></polygon>
                  <polygon
                    fill="#393939"
                    points="70.313 112.5 64.125 125.438 86.063 119.813"
                    transform="matrix(-1 0 0 1 150.188 0)"
                  ></polygon>
                  <polygon
                    fill="#E88F35"
                    points="12.375 .563 88.875 58.5 75.938 27"
                  ></polygon>
                  <path
                    fill="#8E5A30"
                    d="M12.3750002,0.562500008 L2.25000003,31.5000005 L7.87500012,65.250001 L3.93750006,67.500001 L9.56250014,72.5625 L5.06250008,76.5000011 L11.25,82.1250012 L7.31250011,85.5000013 L16.3125002,96.7500014 L58.5000009,83.8125012 C79.1250012,67.3125004 89.2500013,58.8750003 88.8750013,58.5000009 C88.5000013,58.1250009 63.0000009,38.8125006 12.3750002,0.562500008 Z"
                  ></path>
                </g>
              </g>
            </svg>
          </button>
        </div>
        <div className="column">
          {connectedAddress && <p>Connected Address: {connectedAddress}</p>}
        </div>
      </div>
      {/* Viewing of Members Address, Name and Task */}
      <div className="row">
        <div className="column">
          <h2>Team Members</h2>
          <table>
            <thead>
              <tr>
                <th>Address</th>
                <th>Name</th>
                <th>Total Tasks Assigned</th>
                <th>Total Tasks Completed</th>
                <th>Active</th>
              </tr>
            </thead>
            <tbody>
              {teamMembers.map((member, index) => (
                <tr key={index}>
                  <td>{member.walletAddress}</td>
                  <td>{member.name}</td>
                  <td>{member.totalTasksAssigned}</td>
                  <td>{member.totalTasksCompleted}</td>
                  <td>{member.isActive ? "ðŸŸ¢" : "ðŸ”´"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        {/* Adding New Team Member */}
        <div className="column">
          <h2>Add Team Member</h2>
          <form onSubmit={handleAddTeamMember}>
            <label>
              Wallet Address:
              <input
                type="text"
                name="walletAddress"
                value={walletAddress}
                onChange={(e) => setWalletAddress(e.target.value)}
              />
            </label>
            <br />
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </label>
            <br />
            <button type="submit">Add Team Member</button>
          </form>
        </div>
        {/* Assigning Task to a Team Member */}
        <div className="column">
          <h2>Assign Task</h2>
          <form onSubmit={handleAssignTask}>
            <label>
              Team Member Address:
              <input
                type="text"
                value={teamMemberAddress}
                onChange={(e) => setTeamMemberAddress(e.target.value)}
              />
            </label>
            <br />
            <label>
              Task Description:
              <input
                type="text"
                value={taskDescription}
                onChange={(e) => setTaskDescription(e.target.value)}
              />
            </label>
            <br />
            <button type="submit">Assign Task</button>
          </form>
        </div>
        {/* Task Completion */}
        <div className="column">
          <h2>Complete Task</h2>
          <form onSubmit={handleCompleteTask}>
            <label>
              Team Member Address:
              <input
                type="text"
                value={teamMemberAddress}
                onChange={(e) => setTeamMemberAddress(e.target.value)}
              />
            </label>
            <br />
            <button type="submit">Complete Task</button>
          </form>
        </div>
      </div>
      <div className="row">
        {/* Deactivating Team Member */}
        <div className="column">
          <h2>Deactivate Team Member</h2>
          <form onSubmit={handleDeactivateTeamMember}>
            <label>
              Team Member Address:
              <input
                type="text"
                name="teamMember"
                value={teamMemberAddress}
                onChange={(e) => setTeamMemberAddress(e.target.value)}
              />
            </label>
            <br />
            <button type="submit">Deactivate Team Member</button>
          </form>
        </div>
        {/* Releasing Payment */}
        <div className="column">
          <h2>Release Payment</h2>
          <form onSubmit={handleReleasePayment}>
            <label>
              Team Member Address:
              <input
                type="text"
                value={teamMemberAddress}
                onChange={(e) => setTeamMemberAddress(e.target.value)}
              />
            </label>
            <br />
            <label>
              Amount:
              <input
                type="text"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
              />
            </label>
            <br />
            <button type="submit">Release Payment</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ContractTeamManagement;
